mapper.select:
select m.id, m.content, m.send_user_id, m.send_username, m.send_time, m.type_id, m.feather, m.handle_identify, m.handle_username, m.estimate_time, m.finish_time, m.msg_status, m.is_top, mt.name as typeName from m_message m inner join m_msg_send mu on m.id = mu.msgid inner join m_type mt on m.type_id = mt.id where /*个人*/ mu.receive_user_id = #{userId,jdbcType=VARCHAR}
and (mu.isreaded=0 or (m.feather = '1' and msg_status=0))
/*消息分类*/
and m.type_id = #{typeId,jdbcType=VARCHAR}
/*发送时间*/
and to_date(m.send_time, 'yyyy-mm-dd') >= to_date(#{startTime,jdbcType=VARCHAR},'yyyy-mm-dd')
and to_date(m.send_time, 'yyyy-mm-dd') <= to_date(#{endTime,jdbcType=VARCHAR},'yyyy-mm-dd')
order by is_top desc, m.send_time desc

select m.id, m.content, m.send_user_id, m.send_username, m.send_time, m.type_id, m.feather, m.handle_identify, m.msg_status, m.is_top, m.content, mt.name as typename /*讨论组消息、普通消息*/
from m_message m left join m_type mt on m.type_id = mt.id where m.send_user_id = #{userId,jdbcType=VARCHAR} and (m.discussion_group_id is null or m.discussion_group_id = '')
,md.name as discussiongroupname from m_message m left join m_discussion_group md on m.discussion_group_id = md.id left join m_type mt on m.type_id = mt.id where m.send_user_id = #{userId,jdbcType=VARCHAR} and m.discussion_group_id is not null and m.discussion_group_id != ''
/*消息分类*/
and m.type_id = #{typeId,jdbcType=VARCHAR}
/*发送时间*/
and to_date(m.send_time, 'yyyy-mm-dd') >= to_date(#{startTime,jdbcType=VARCHAR},'yyyy-mm-dd')
and to_date(m.send_time, 'yyyy-mm-dd') <= to_date(#{endTime,jdbcType=VARCHAR},'yyyy-mm-dd')
order by m.send_time desc

mapper.update:
update m_message
set is_top = #{isTop,jdbcType=VARCHAR}
where id in (${ids})

mapper.select:
select m.id, m.content, m.send_time, m.send_user_id, m.send_username, m.feather, m.handle_identify, m.handle_result, m.handle_username, m.handle_user_id, m.data_source, m.estimate_time, m.exception_table, m.finish_time, m.msg_status, mu.receive_user_id, mu.receive_username, mu.handle_identify as receivetype from m_message m left join m_msg_user mu on /*讨论组或者普通消息*/
m.id = mu.msg_id where m.id = #{id,jdbcType=VARCHAR}
m.discussion_group_id = mu.discussion_group_id where m.id = #{id,jdbcType=VARCHAR} and mu.is_delete = '0'
