mapper.select:
select CONCAT("立案：", IFNULL(GROUP_CONCAT(WHY_CASE),"无\n")) as description from oms_sup_case_info where 1=1 and DISCIPLINARY_ACTION = '0'
and A0100 = #{a0100}
union ALL select CONCAT("处分：", IFNULL(GROUP_CONCAT(WHY_DISCIPLINARY),"无\n")) as description from oms_sup_disciplinary where 1=1
and A0100 = #{a0100}
and DISCIPLINARY_END_TIME >= #{abroadTime}
union ALL select CONCAT("违反外事纪律：", IFNULL(GROUP_CONCAT(DESCRIPTION),"无\n")) as description from oms_sup_violation_discipline where 1=1
and A0100 = #{a0100}
and VIOLATION_END_TIME >= #{abroadTime}
union ALL select CONCAT("免职撤职：", IFNULL(GROUP_CONCAT(REASON),"无")) as description from oms_sup_dismissed where 1=1
and A0100 = #{a0100}
