mapper.select:
SELECT m.ID AS 'id', o.B0101 AS 'WORK_UNIT', o.NAME AS 'NAME', o.SEX AS 'SEX', o.BIRTH_DATE AS 'BIRTH_DATE', o.JOB AS 'JOB', o.POLITICAL_AFFI AS 'POLITICAL_AFFI', m.Adjustment_time AS 'Adjustment_time', m.Completion_time AS 'Completion_time', m.STATUS AS 'status' FROM oms_personinfo o, oms_mobilizingcadre m WHERE m.A0100 = o.A0100 AND m.b0100 = o.b0100
and o.b0111 in
#{orgIds}
and o.name like CONCAT('%',#{name},'%')
and m.status = #{status}

mapper.update:
update oms_mobilizingcadre
set status = #{status,jdbcType=CHAR}
where a0100 = #{a0100,jdbcType=VARCHAR}
