mapper.select:
SELECT b.b0101 as '单位' , a.A0101 AS '姓名', a.A0104 AS '性别', a.A0107 AS '出生年月' , a2.A0215A as '职务职级' , a.a0141 AS '政治面貌' , m.Adjustment_time AS '调整时间' , m.Completion_time as '结束时间' , m.status AS '状态' FROM oms_mobilizingcadre m INNER JOIN a01 a ON m.A0100 = a.a0100 INNER JOIN a02 a2 on a2.a0100 = a.A0100 INNER JOIN b01 b on a2.a0201b = b.b0111
and b.b0111 in
#{orgIds}
and (a.A0101 like CONCAT('%',#{name},'%')
and m.status = #{status}
