mapper.select:
select "1.注册" as label, "0" as statusCode, count(0) as num from cf_user where user_state = "0"

  and org_id = #{orgId}

union all
select "2.上报" as label, "0" as statusCode, count(0) as num from cf_user where user_state = "0"

  and org_id = #{orgId}

union all
select "3.监督处审核" as label, "3" as statusCode, count(0) as num from cf_user where user_state = "3"

  and org_id = #{orgId}

union all
select "4.处领导审核" as label, "4" as statusCode, count(0) as num from cf_user where user_state = "4"

  and org_id = #{orgId}

union all
select "5.身份认证" as label, "8" as statusCode, count(0) as num from cf_user where user_state = "8"

  and org_id = #{orgId}

select "1.撤销经办人" as label, "0" as statusCode, count(0) as num from oms_operator_handover o INNER JOIN cf_user cf on o.OperatorId=cf.user_id where o.HandoverStatus = "0"

  and cf.org_id = #{orgId}

union all
select "2.经办人交接" as label, "3" as statusCode, count(0) as num from oms_operator_handover o INNER JOIN cf_user cf on o.OperatorId=cf.user_id where o.HandoverStatus = "3"

  and cf.org_id = #{orgId}

union all
select "3.接手人确认" as label, "4" as statusCode, count(0) as num from oms_operator_handover o INNER JOIN cf_user cf on o.HandoverId=cf.user_id where o.HandoverStatus = "4"

  and cf.org_id = #{orgId}

union all
select "4.管理员确认" as label, "5" as statusCode, count(0) as num from oms_operator_handover o INNER JOIN cf_user cf on o.HandoverId=cf.user_id where o.HandoverStatus = "5"

  and cf.org_id = #{orgId}

mapper.insert:
insert into oms_operator_approval (ID, OperatorId, StepName, 
  SubmissionTime, Submitter, ApprovalDate, 
  Approver, ApprovalResult, ApprovalOpinion
  )
values (#{id,jdbcType=VARCHAR}, #{operatorid,jdbcType=VARCHAR}, #{stepname,jdbcType=VARCHAR}, 
  #{submissiontime,jdbcType=DATE}, #{submitter,jdbcType=VARCHAR}, #{approvaldate,jdbcType=DATE}, 
  #{approver,jdbcType=VARCHAR}, #{approvalresult,jdbcType=VARCHAR}, #{approvalopinion,jdbcType=VARCHAR}
  )
