mapper.select:
select opa.*, CONCAT(IFNULL(orp.SURNAME,''),IFNULL(orp.NAME,'')) as name, orp.BIRTH_DATE as birthDate, orp.SEX as sex, orp.IDNUMBER_GB as idnumber, orp.REGISTE_RESIDENCE as registeResidence, orp.WORK_UNIT as b0101, orp.IDENTITY as identifyType from oms_pri_apply opa left join oms_reg_procpersoninfo orp on opa.PROCPERSON_ID = orp.ID
and opa.CREATE_USER = #{name}
and opa.APPLY_STATUS <> #{status}

select pa.*, rp.SEX as sex, CONCAT(IFNULL(rp.SURNAME,''),IFNULL(rp.NAME,'')) as name, rp.IDNUMBER_GB as idnumber, rp.WORK_UNIT as b0101, rp.POST as postrank from oms_pri_delay_apply pa LEFT JOIN oms_reg_procpersoninfo rp on pa.PROCPERSON_ID = rp.ID
and pa.CREATE_USER = #{userId}
and pa.APPLY_STATUS <> #{status}

SELECT b.* ,a.HandoverStatus as handoverStatus
FROM oms_operator_handover a
INNER JOIN oms_operator_handover_subform b ON a.ID=b.HandoverFormID


    AND a.OperatorId = #{userId,jdbcType=VARCHAR}
    or a.HandoverId = #{userId,jdbcType=VARCHAR}

SELECT b.* ,a.HandoverStatus as handoverStatus
FROM oms_operator_handover a
INNER JOIN oms_operator_handover_subform b ON a.ID=b.HandoverFormID


    and a.OperatorId = #{operatorId,jdbcType=VARCHAR}


    and a.HandoverStatus = #{handoverStatus}

SELECT b.* ,a.HandoverStatus as handoverStatus
FROM oms_operator_handover a
INNER JOIN oms_operator_handover_subform b ON a.ID=b.HandoverFormID


  and a.HandoverId = #{handoverId,jdbcType=VARCHAR}


  and a.HandoverStatus = #{handoverStatus}

select

from oms_operator_handover
where
  HandoverStatus != '1'

    and OperatorId = #{operatorId}


    and HandoverId = #{handoverId}

SELECT * from oms_reg_revokeapply


    and oms_reg_revokeapply.CREATE_USER = #{userId}


    and oms_reg_revokeapply.STATUS <> #{status}

SELECT ocgt.*, rp.IDNUMBER_GB as idnumber, rp.WORK_UNIT as b0101, rp.POST as postrank FROM oms_cer_get_task ocgt LEFT JOIN oms_reg_procpersoninfo rp on ocgt.OMS_ID = rp.ID LEFT JOIN oms_cer_exit_entry_repertory oce on ocgt.ID = oce.GET_ID
AND ocgt.GET_PEOPLE = #{userId}
AND oce.STATUS = #{status}

SELECT *
FROM oms_cer_cancellate_license occl


    AND occl.CREATE_USER = #{userId}


    AND occl.ZHZXZT <> #{status}

mapper.update:
update oms_operator_handover
set OperatorId = #{operatorid,jdbcType=VARCHAR},
  HandoverId = #{handoverid,jdbcType=VARCHAR},
  HandoverTime = #{handovertime,jdbcType=DATE},
  HandoverStatus = #{handoverstatus,jdbcType=CHAR}
where ID = #{id,jdbcType=VARCHAR}
