mapper.select:
SELECT t.*,r.WORK_UNIT as b0101,CONCAT(r.SURNAME,r.`NAME`) as a0101 FROM oms_smr_oldinfo t LEFT JOIN oms_reg_procpersoninfo r ON t.A0100 = r.A0100 WHERE 1 = 1
AND concat(r.SURNAME,r.NAME) like #{name}
AND r.PY like #{namePy}
and r.rf_B0000 = #{orgId} and IFNULL(t.SFQR,'0') = '0' and ISNULL(START_DATE)=FALSE

SELECT t.*,r.WORK_UNIT as b0101,CONCAT(r.SURNAME,r.`NAME`) as a0101 FROM oms_smr_oldinfo t LEFT JOIN oms_reg_procpersoninfo r ON t.A0100 = r.A0100 WHERE 1 = 1
AND concat(r.SURNAME,r.NAME) like #{name}
AND r.PY like #{namePy}
and r.RF_B0000 = #{orgId} and t.SECRET_RELATED_LEVEL is null and r.INCUMBENCY_STATUS = '1'

SELECT * FROM ( SELECT t.id, t.RF_B0000 b0100, '系统' data_source, b.b0101, concat( t.SURNAME, t.`NAME` ) NAME, case t.SEX when 1 then '男' when 2 then '女' end sex, t.BIRTH_DATE, t.POST, t.IDNUMBER_GB ID_CARD_NUMBER, '无涉密信息' msg, a.SECRET_RELATED_LEVEL, a.SECRET_RELATED_POST, a.START_DATE, a.FINISH_DATE FROM oms_reg_procpersoninfo t INNER JOIN b01 b ON t.RF_B0000 = b.b0100 LEFT JOIN oms_smr_oldinfo a ON t.id = a.PROC_PERSON_ID AND a.IMPORT_YEAR = #{importYear} and a.b0100 = #{b0100} WHERE t.DATA_TYPE = '1' AND t.SECRET_LEVEL IS NULL AND t.INBOUND_FLAG != 'D' AND t.RF_B0000 = #{b0100}
UNION ALL
SELECT t.id, t.B0100, '保密局' data_source, b.b0101, t.NAME, t.SEX, t.BIRTH_DATE, t.post, t.ID_CARD_NUMBER, '未匹配到干部' msg, t.SECRET_RELATED_LEVEL, t.SECRET_RELATED_POST, t.START_DATE, t.FINISH_DATE FROM oms_smr_recordinfo t INNER JOIN b01 b ON t.b0100 = b.b0100 WHERE t.IS_MATCHING = '0' AND t.IMPORT_YEAR = #{importYear} AND t.B0100 = #{b0100} ) a ORDER BY a.NAME
