mapper.select:
SELECT t.ID, t.A0100, t.B0100, (select b.b0101 from b01 b where t.b0100 = b.B0100) b0101, concat(r.SURNAME,r.NAME) as NAME, case r.SEX when '1' then '男' when '2' then '女' end sex, t.ID_CARD_NUMBER, t.A0141, t.POST, t.PERSON_STATE, t.SECRET_RELATED_POST, t.SECRET_RELATED_LEVEL, t.SECRET_REVIEW_DATE, (select b.b0101 from b01 b where b.b0100 = t.SECRET_RELATED_ORG) SECRET_RELATED_ORG, (select b.b0101 from b01 b where b.b0100 = t.MAX_SECRET_RELATED_ORG) MAX_SECRET_RELATED_ORG, t.MAX_SECRET_RELATED_LEVEL, t.MAX_FINISH_DATE, t.START_DATE, t.FINISH_DATE, t.UPDATE_USER_ID, t.UPDATE_TIME, t.IMPORT_YEAR FROM oms_smr_personinfo t LEFT JOIN oms_reg_procpersoninfo r ON t.A0100 = r.A0100 WHERE 1 = 1
AND concat(r.SURNAME,r.NAME) like #{name}
AND r.PY like #{namePy}
AND t.B0100 = #{b0100}
AND t.SECRET_RELATED_LEVEL = #{level}
AND t.SECRET_RELATED_POST = #{post}
AND t.PERSON_STATE = #{personState}
AND t.B0100 IN
#{item}
ORDER BY t.UPDATE_TIME DESC

SELECT t.* FROM oms_smr_oldinfo t LEFT JOIN oms_reg_procpersoninfo r ON t.A0100 = r.A0100 WHERE 1 = 1
AND concat(r.SURNAME,r.NAME) like #{name}
AND r.PY like #{namePy}
and r.RF_B0000 = #{orgId} and t.SECRET_RELATED_LEVEL is not null and r.INCUMBENCY_STATUS = '1'
