SELECT c.id, a.id cerId, b.id omsId, b.work_unit workUnit, b.rf_B0000 rfB0000, a.name, if(a.sex='1','男',if(a.sex='2','女',a.sex)) sex, b.incumbency_status incumbencyStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZZT' AND b.incumbency_status= item.item_code ) incumbencyStatusName, b.post, a.zjlx, (SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZJLX' AND a.zjlx= item.item_code) zjlxName, a.xplx, if(a.xplx='0','自带',if(a.xplx='1','粘贴',a.xplx)) xplxName, a.zjxs, if(a.zjxs='0','本式',if(a.zjxs='1','卡式',a.zjxs)) zjxsName, a.zjhm, a.yxqz, (select user_name from cf_user where user_id=c.operator) operator, c.get_cause getCause, c.operate_time operateTime, d.get_time getTime, a.surely_Unit surelyUnit, a.card_Status cardStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZJZT' AND a.card_Status= item.item_code ) cardStatusName, a.save_Status saveStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZBGZT' AND a.save_Status= item.item_code ) saveStatusName, a.surely_Way surelyWay, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZBGFS' AND a.surely_Way= item.item_code ) surelyWayName, a.cabinet_Num cabinetNum, a.place, a.counter_num counterNum, a.csrq, a.qfjg, a.qfrq, a.csdd, a.exception_Message exceptionMessage FROM OMS_CER_ADMINTOR_GET_APPLY c INNER JOIN cf_certificate a ON c.cer_id=a.id INNER JOIN oms_reg_procpersoninfo b ON a.oms_id = b.id LEFT JOIN OMS_CER_GET_TASK d ON c.id=d.busi_id
a.name in #{item}
AND b.rf_B0000=#{rfB0000}
AND a.zjlx=#{zjlx}
AND a.zjhm=#{zjhm}
AND a.card_Status=#{cardStatus}
AND d.operator=#{operator}
AND d.operateTime >= #{operateStartTime}
AND d.operateTime <= #{operateEndTime}

