mapper.select:
SELECT ID, COLLECTION_ID, CF_ID, COLLECTION_TIME, COLLECTION_TYPE, RESPONSIBLE_USER, RESPONSIBLE_PHONE, SAVE_TIME, COLLECTION_CONTEXT FROM cf_certificate_collection where 1=1
AND ID = #{id}
AND COLLECTION_TYPE = #{collectionType}
AND CF_ID = #{cfId}
AND RESPONSIBLE_USER = #{responsibleUser}

SELECT a.id, a.oms_id omsId, a.name, if(c.sex='1','男',if(c.sex='2','女',c.sex)) sex, c.birth_date csrq, c.idnumber_Gb idnumberGb, c.registe_Residence registeResidence, c.Rf_B0000 rfB0000, c.work_Unit workUnit, c.post, c.person_Manager personManager, a.cj_Status cj_Status, (select item_name from sys_dict_item where dict_code='ZJCJZT' and item_code=a.cj_Status) cjStatusName, d.user_name handler, d.user_mobile handlerPhone, a.zjlx, (select item_name from sys_dict_item where dict_code='ZJLX' and item_code=a.zjlx) zjlxName, a.zjhm, a.data_source dataSource, (select item_name from sys_dict_item where dict_code='ZJCJSJLY' and item_code=a.data_source) dataSourceName, a.happen_Date happenDate, a.return_Date returnDate, a.cj_Result cjResult FROM cf_certificate_collection a INNER JOIN oms_reg_procpersoninfo c ON a.oms_id = c.id LEFT JOIN cf_user d ON a.rf_b0000 = d.org_id
AND a.name=#{name}
AND a.work_Unit=#{workUnit}
AND a.cj_Status=#{cjStatus}
AND a.data_Source=#{dataSource}
AND a.return_Date>=#{returnStartDate}
AND a.return_Date<=#{returnEndDate}
GROUP BY id ORDER BY return_date
