mapper.select:
SELECT a.id, a.name, if(a.sex='1','男',if(a.sex='2','女',a.sex)) sex, b.work_unit workUnit, b.incumbency_status incumbencyStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZZT' AND b.incumbency_status= item.item_code ) AS incumbencyStatusName, b.post, a.zjlx, (SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZJLX' AND a.zjlx= item.item_code) zjlxName, a.xplx, if(a.xplx='0','自带',if(a.xplx='1','粘贴',a.xplx)) xplxName, a.zjxs, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZJXS' AND a.zjxs= item.item_code ) AS zjxsName, a.zjhm, a.yxqz, a.surely_Unit surelyUnit, if(a.surely_Unit='0','干部监督处',if(a.surely_Unit='1','省委统战部(台办)',a.surely_Unit)) surelyUnitName, a.card_Status cardStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZJZT' AND a.card_Status= item.item_code ) AS cardStatusName, a.save_Status saveStatus, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZBGZT' AND a.save_Status= item.item_code ) AS saveStatusName, a.surely_Way surelyWay, ( SELECT item.item_name FROM sys_dict_item item WHERE item.dict_code = 'ZZBGFS' AND a.surely_Way= item.item_code ) AS surelyWayName, a.cabinet_Num cabinetNum, a.place, a.counter_num counterNum, a.csrq, a.qfjg, a.qfrq, a.csdd, a.exception_Message exceptionMessage FROM cf_certificate a INNER JOIN oms_reg_procpersoninfo b ON a.oms_id = b.id
a.name=#{name}
AND b.rf_B0000=#{rfB0000}
AND a.zjlx=#{zjlx}
AND a.zjhm=#{zjhm}
AND a.yxqz=#{yxqz}
AND a.save_Status=#{saveStatus}
AND a.card_Status=#{cardStatus}
AND a.zjxs=#{zjxs}
AND a.xplx=#{xplx}
AND a.id in
#{item}

SELECT
    1 from cf_certificate
WHERE
    zjlx = #{zjlx}
AND zjhm = #{zjhm}
