mapper.select:
oms_cer_print_qr_code o INNER JOIN oms_cer_get_task a ON o.GET_ID=a.id INNER JOIN cf_certificate b ON a.cer_id = b.id INNER JOIN oms_reg_procpersoninfo c ON a.oms_id = c.id WHERE o.QR_CODE_ID=#{qrCodeId}
union
cf_user o INNER JOIN oms_cer_get_task a ON o.org_id=a.RF_B0000 INNER JOIN cf_certificate b ON a.cer_id = b.id INNER JOIN oms_reg_procpersoninfo c ON a.oms_id = c.id AND a.get_status='0' AND a.data_source ='3'

SELECT
    1
FROM
    cf_user
WHERE
    user_state = '1'
AND user_name = #{name}
AND IDNUMBER = #{idNo}
AND user_id = #{operatId}
