mapper.select:
select pa.*, CONCAT(IFNULL(rp.SURNAME,''),IFNULL(rp.NAME,'')) as name, rp.SEX as sex, rp.BIRTH_DATE as birthDate, rp.WORK_UNIT as b0101, rp.IDENTITY as identifyType from oms_pri_apply pa LEFT JOIN oms_reg_procpersoninfo rp on pa.A0100 = rp.A0100 left join oms_reg_procpersoninfo rp1 on pa.CREATE_USER = rp1.A0100 where 1=1
and pa.APPLY_TIME > #{omsPriApplyIPageParam.applyStartTime} and pa.APPLY_TIME < #{omsPriApplyIPageParam.applyStopTime}
and pa.ABROAD_TIME > #{omsPriApplyIPageParam.abroadStartTime} and pa.ABROAD_TIME < #{omsPriApplyIPageParam.abroadEndTime}
and CONCAT(IFNULL(rp.SURNAME,''),IFNULL(rp.NAME,'')) LIKE CONCAT('%',#{omsPriApplyIPageParam.name},'%') and rp.PY LIKE CONCAT('%',#{omsPriApplyIPageParam.name},'%')
and CONCAT(IFNULL(rp1.SURNAME,''),IFNULL(rp1.NAME,'')) LIKE CONCAT('%',#{omsPriApplyIPageParam.createUser},'%') and rp1.PY LIKE CONCAT('%',#{omsPriApplyIPageParam.createUser},'%')
and pa.APPLY_STATUS in
#{item}
order by pa.APPLY_STATUS, pa.ABROAD_TIME
