mapper.update:
update oms_pub_apply p


        p.CXYY = #{cxyy},


        p.SQZT = #{sqzt}


where p.PWH = #{pwh}

mapper.select:
select * from oms_pub_apply p where p.cgsj
=(select max(cgsj) from oms_pub_apply where a0100=#{a0100})
and a0100 = #{a0100}

select oms_pub_apply.* ,concat(oms_reg_procpersoninfo.surname,oms_reg_procpersoninfo.name) as "NAME" ,oms_reg_procpersoninfo.SEX as sex ,oms_reg_procpersoninfo.BIRTH_DATE_GB as birthDate ,oms_reg_procpersoninfo.NATION_NAME ,oms_reg_procpersoninfo.POLITICAL_AFFINAME ,oms_reg_procpersoninfo.WORK_UNIT as b0101
,oms_reg_procpersoninfo.SECRET_LEVEL AS SECRET_LEVEL ,op.SECRET_REVIEW_DATE AS SECRET_REVIEW_DATE ,oms_reg_procpersoninfo.NF AS NF from oms_pub_apply left join oms_reg_procpersoninfo on oms_pub_apply.PROCPERSON_ID=oms_reg_procpersoninfo.ID LEFT JOIN oms_smr_personinfo op ON oms_reg_procpersoninfo.id = op.PROC_PERSON_ID where oms_pub_apply.SFXD = '1'
and oms_pub_apply.ID = #{omsPubApplyQueryParam.applyId}
and oms_pub_apply.SQZT in
#{status}
and concat(oms_reg_procpersoninfo.surname,oms_reg_procpersoninfo.name) like CONCAT('%',#{omsPubApplyQueryParam.name},'%')
and oms_pub_apply.CGSJ >= to_date(#{omsPubApplyQueryParam.cgsj,jdbcType=DATE},'yyyy-MM-dd hh24:mi:ss')
and oms_pub_apply.CGSJ <= to_date(#{omsPubApplyQueryParam.hgsj,jdbcType=DATE},'yyyy-MM-dd hh24:mi:ss')
and oms_pub_apply.ZTDW = #{omsPubApplyQueryParam.ztdw}
and oms_pub_apply.pwh like CONCAT('%',#{omsPubApplyQueryParam.pwh},'%')
and oms_pub_apply.b0100 = #{omsPubApplyQueryParam.b0100}
and oms_pub_apply.PROCPERSON_ID = #{omsPubApplyQueryParam.procpersonId}
and oms_pub_apply.IS_COMPARISON = #{omsPubApplyQueryParam.isComparison}

select oms_pub_apply.* ,concat(oms_reg_procpersoninfo.surname,oms_reg_procpersoninfo.name) as "NAME" ,oms_reg_procpersoninfo.SEX ,oms_reg_procpersoninfo.BIRTH_DATE_GB ,oms_reg_procpersoninfo.NATION_NAME ,oms_reg_procpersoninfo.POLITICAL_AFFINAME ,oms_reg_procpersoninfo.WORK_UNIT as b0101 from oms_pub_apply left join oms_reg_procpersoninfo on oms_pub_apply.PROCPERSON_ID=oms_reg_procpersoninfo.ID
and oms_pub_apply.CREATE_USER = #{createUser}
and oms_pub_apply.SQZT <> #{sqzt}

select oms_pub_apply.* ,concat(oms_reg_procpersoninfo.surname,oms_reg_procpersoninfo.name) as "NAME" ,oms_reg_procpersoninfo.SEX ,oms_reg_procpersoninfo.BIRTH_DATE_GB ,oms_reg_procpersoninfo.NATION_CODE ,oms_reg_procpersoninfo.POLITICAL_AFFINAME ,oms_reg_procpersoninfo.WORK_UNIT as b0101 from oms_pub_apply left join oms_reg_procpersoninfo on oms_pub_apply.PROCPERSON_ID=oms_reg_procpersoninfo.ID
and oms_pub_apply.CGSJ >= now() and oms_pub_apply.PWH like CONCAT('%',#{pwh},'%')
and oms_pub_apply.SQZT <> #{sqzt}

SELECT a.a0101 AS NAME, a.a0184 AS idnumber, a.a0100 AS a0100, a.A0104 AS sex, a.A0107 AS birthDate, a.a0192a AS job, b.b0111 AS b0111, b.b0101 AS b0101, b.b0100 AS b0100, `GETDICTITEM` ( '07', `a`.`a0141`, '0' ) AS `POLITICAL_AFFI`, `GETDICTITEM` ( '01', `a`.`a0117`, '0' ) AS `NATION` FROM a01 a JOIN a02 a2 ON a2.a0100 = a.a0100 and a2.A0255='1' AND a2.a0281 = 'true' and a2.A0279='1' JOIN b01 b ON a2.a0201b = b.b0100
and (a.a0101 like CONCAT('%',#{keyword},'%') or a.a0102 like CONCAT('%',#{keyword},'%'))
and a.a0165 in
#{a0165}
and b.b0100 in
#{b0100}

SELECT CONCAT( IFNULL( orp.SURNAME, '' ), IFNULL( orp.NAME, '' ) ) AS NAME FROM oms_pub_apply opa, ( SELECT * FROM oms_pub_apply o1 WHERE o1.PWH IN ( SELECT PWH FROM oms_pub_apply WHERE
ID = #{a0100}
) AND o1.ZTNRZW = '团长' ) p LEFT JOIN oms_reg_procpersoninfo orp ON p.PROCPERSON_ID = orp.ID WHERE
opa.ID = #{a0100}

